        {%endif%}
      {%endif%}


      {%endfor%}
    {%for i in l.tax_ids%}
      {%if not i.name.startswith('IVA') and not i.name.startswith('IEPS') %}
        {%if i.amount > 0%}
          {% if cantidadOtros.update({"value": cantidadOtros.value + ((i.amount/100) * (l.price_subtotal)) })%}{% endif %}
        {%endif%}
      {%endif%}
    {%endfor%}

  {%endfor%}
